using System.Net;
using System.Text.Json;

string baseUrl = "https://smseagleip/api/v2/";
string method = "messages/sms";

string url = baseUrl + method;

string[] numbers =
{
	"+481234567"
};

int[] contacts = 
{
  12,
  15
};

int[] groups =
{
  57,
  35
};

string date = "2022-12-27T12:20:31+05:01";
int priority = 1;
string encoding = "unicode";
bool flash = true;
string oid = "123abc456def789ghi123abc456def789ghi";
string validity = "30m";
string send_after = "12:05";
string send_before = "17:15";
bool test = true;
int modem_no = 2;

string message = "My message";

WebClient client = new WebClient();

string json = JsonSerializer.Serialize(new
{
	to = numbers,
    contacts = contacts,
    groups = groups,
	text = message,
    date = date,
    priority = priority,
    encoding = encoding,
    flash = flash,
    oid = oid,
    validity = validity,
    send_after = send_after,
    send_before = send_before,
    test = test,
    modem_no = modem_no
});

client.Headers[HttpRequestHeader.ContentType] = "application/json";
client.Headers.Add("access-token", "0005gOjCOlMH8F2BP8");
string response = client.UploadString(new Uri(url), "POST", json);

return response;

import java.io.*;
import java.net.*;
import org.json.JSONObject;

public class Main {
    public static void main(String[] args) {
        try {
            HttpURLConnection connection = null;
            OutputStreamWriter writer = null;
            BufferedReader reader = null;
            StringBuilder sbuilder = null;
            URL serverAddress = null;
            serverAddress = new URL("https://url-of-smseagle/index.php/api/v2/messages/sms");
            connection = (HttpURLConnection) serverAddress.openConnection();
            connection.setRequestProperty("Content-Type", "application/json");
            connection.setRequestProperty("Accept", "application/json");
            connection.setRequestProperty("access-token", "0005gOjCOlMH8F2BP8");
            connection.setRequestMethod("POST");
            connection.setDoOutput(true);
            connection.connect();
//
            writer = new OutputStreamWriter(connection.getOutputStream());

            JSONObject json = new JSONObject();

            String[] to = {"481234567"};
            json.put("to", to);
            json.put("text", "My message");

            System.out.println(json);
            writer.write(json.toString());
            writer.close();
//
            reader = new BufferedReader(new InputStreamReader(connection.getInputStream()));
            sbuilder = new StringBuilder();
            String line = null;
            while ((line = reader.readLine()) != null) {
                sbuilder.append(line + 'n');
            }
            connection.disconnect();
            reader.close();
            String result = sbuilder.toString();
            System.out.println(result);
        } catch (Exception e) {
            System.out.println(e);
        }
    }
}

